INSERT INTO isearch_kelowna VALUES (168,'marker',168,'marker',999,'jon','jon.corbett@gmail.com',2,'Housing Agencies','CMHA Kelowna','49.8794484, -119.4931996','Housing and Navigation Services:Having a safe, affordable roof over your head is something to which everyone should have access. Navigating the many resources to find housing, financial assistance, health care, and specific services to promote wellness and recovery can be extremely difficult without help. CMHA Kelowna strives to break the cycle of homelessness by supporting people to find a good place to call home. In addition, our team works in collaboration with the community to ensure that people have the supports necessary to maintain their housing long term.',0,'link to 168','2017-03-28 15:36:00','2017-06-29 22:43:00','Super User',0,'public',101,NULL,170,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'PovertyAddictionMental illness.',NULL,'http://cmhakelowna.com/housing/',NULL,NULL,NULL,'250-861-3644','http://cmhakelowna.com/contact-us/',NULL);
